import React, { useState } from 'react';
import styles from './Portfolio.module.css';

interface Project {
  id: number;
  title: string;
  image: string;
  description: string;
}

const Portfolio: React.FC = () => {
  const [flippedCard, setFlippedCard] = useState<number | null>(null);

  const projects: Project[] = [
    {
      id: 1,
      title: 'Blog sobre Cocina',
      image: '/img/BlogCocina.png',
      description: 'Como parte del curso de desarrollo web en el Informatorio, creé un blog de cocina con Django y Python. Incluyó gestión de usuarios con roles diferenciados (usuarios registrados, colaboradores y administradores), publicación y organización de recetas, espacio para comentarios, y medidas de seguridad para restringir acciones según el rol. Este proyecto destacó por la implementación de permisos personalizados y la aplicación de conceptos avanzados de desarrollo colaborativo.'
    },
    {
      id: 2,
      title: 'Dashboard de Análisis de Datos',
      image: '/img/Dashboard.jpg',
      description: 'Como parte del curso de Data Analytics en el Informatorio, desarrollé un dashboard interactivo en Power BI que permitió analizar datos reales y responder preguntas clave sobre ventas, clientes y productos. El proyecto incluyó exploración de datos, creación de visualizaciones dinámicas, optimización del modelo de datos y diseño de informes estratégicos, demostrando mi habilidad para transformar datos complejos en insights accionables mediante herramientas avanzadas de análisis.'
    },
    {
      id: 3,
      title: 'JuliJo - Showroom de Venta de Ropas',
      image: '/img/Showroom.png',
      description: 'Desarrollé un sistema en .NET para un showroom de venta de ropa, con funcionalidades como gestión de ventas, control de stock y generación de reportes estratégicos. Este sistema centralizó y automatizó procesos, mejorando la eficiencia operativa y ofreciendo una experiencia fluida tanto para el negocio como para los clientes.'
    },
    {
      id: 4,
      title: 'MrBrook - Hamburgueseria',
      image: '/img/Hamburgueseria.png',
      description: 'Desarrollé un sistema integral para una hamburguesería que optimizó la gestión de clientes, inventario, pedidos y facturación. Incluyó módulos para registrar clientes, controlar el stock de insumos con alertas, gestionar pedidos en tiempo real y emitir tickets con reportes diarios. Utilicé SQL Server para implementar una base de datos relacional eficiente, garantizando la integridad y el rendimiento de los datos, mejorando significativamente la operatividad del negocio.'
    },
    {
      id: 5,
      title: 'Griffith\'s - Peluqueria',
      image: '/img/Peluqueria.png',
      description: 'Un sistema integral para peluquerías que optimiza la gestión del negocio mediante funcionalidades como control de stock, administración de promociones, registro de clientes, gestión de turnos y servicios personalizados. También incluye reportes detallados para apoyar la toma de decisiones estratégicas y mejorar la experiencia del cliente.'
    }
  ];

  const handleCardClick = (projectId: number) => {
    setFlippedCard(flippedCard === projectId ? null : projectId);
  };

  return (
    <section id="portfolio" className={styles.portfolio}>
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>PROYECTOS</h2>
        
        <div className={styles.gallery}>
          {projects.map((project) => (
            <div 
              key={project.id}
              className={`${styles.projectCard} ${flippedCard === project.id ? styles.flipped : ''}`}
              onClick={() => handleCardClick(project.id)}
            >
              <div className={styles.cardFace}>
                <div className={styles.cardFront}>
                  <img src={project.image} alt={project.title} />
                  <h3>{project.title}</h3>
                  <div className={styles.cardOverlay}>
                    <i className="fa-solid fa-eye"></i>
                    <span>Ver detalles</span>
                  </div>
                </div>
                <div className={styles.cardBack}>
                  <h3>{project.title}</h3>
                  <p>{project.description}</p>
                  <div className={styles.cardOverlay}>
                    <i className="fa-solid fa-arrow-left"></i>
                    <span>Volver</span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
